INSERT INTO `phome_ecms_shop` VALUES (2, 46, 0, 0, 0, 0, '', '13551244392', 2, 'test', 0, 0, 1, 0, 0, 0, 0, 1355124447, 1355124447, 1, 0, 0, '', '/ecms75/shop/shoujishuma/13551244392.html', 1, 1, 1, '', '诺基亚手机 6120', 1355124447, 'http://www.gome.com.cn/upload/2008/2/24/120_120_20080224101312939.jpg', 0, '', '诺基亚', '', '', '', '1793.00', '1793.00', 0, 100, 'http://www.gome.com.cn/upload/2008/2/24/mid_20080224101312939.jpg', '主要功能<br />*&nbsp;通过发送和接收带有附件的电子邮件掌控繁忙的生活&nbsp;<br />*&nbsp;使用摄像机捕捉全部动作&nbsp;<br />*&nbsp;使用带有闪光灯且支持全景图像的&nbsp;200&nbsp;万像素照相机拍摄相片&nbsp;<br />*&nbsp;通过最高达&nbsp;2GB&nbsp;的可扩展存储空间来储存音乐、视频片段和图像&nbsp;<br />*&nbsp;借助实用的日历和便捷的&nbsp;PC&nbsp;同步功能使万事井井有条、有条不紊&nbsp;<br />*&nbsp;使用音乐播放器欣赏喜爱的音乐，在悦耳的旋律中放松心神，恢复活力&nbsp;<br />*&nbsp;采用镀铬抛光，成熟而经典的设计&nbsp;<br /><br />&nbsp;&nbsp;工作频率<br />*&nbsp;EGSM&nbsp;850/900/1800/1900&nbsp;<br />&nbsp;&nbsp;尺寸<br />*&nbsp;体积：66&nbsp;立方厘米&nbsp;<br />*&nbsp;重量：89&nbsp;克&nbsp;<br />*&nbsp;尺寸：105&nbsp;x&nbsp;46&nbsp;x&nbsp;15&nbsp;毫米&nbsp;<br />&nbsp;&nbsp;屏幕<br />*&nbsp;大屏幕：QVGA、TFT、1600&nbsp;万真彩色、320&nbsp;x&nbsp;240&nbsp;像素、2&nbsp;英寸&nbsp;<br />*&nbsp;当前待机模式&nbsp;<br />*&nbsp;自动键盘锁&nbsp;<br />&nbsp;&nbsp;用户界面<br />*&nbsp;用户界面基于&nbsp;Symbian&nbsp;操作系统的&nbsp;60&nbsp;系列软件&nbsp;<br />&nbsp;&nbsp;图像功能<br />*&nbsp;在横向模式下使用&nbsp;200&nbsp;万像素照相机&nbsp;<br />*&nbsp;数字变焦&nbsp;（4&nbsp;倍）&nbsp;和照相机闪光灯&nbsp;<br />*&nbsp;播放采用&nbsp;H.264&nbsp;（MPEG4）、3gpp、Real&nbsp;编解码器的影音流媒体&nbsp;<br />*&nbsp;录制&nbsp;H.263&nbsp;（3gpp）&nbsp;和&nbsp;MPEG4&nbsp;格式的视频片段&nbsp;<br />&nbsp;&nbsp;多媒体功能<br />*&nbsp;支持&nbsp;MP3、M4A、eAAC+&nbsp;和&nbsp;WMA&nbsp;的音乐播放器&nbsp;<br />*&nbsp;支持播放符合&nbsp;3GPP&nbsp;标准的影音流媒体&nbsp;<br />*&nbsp;铃声：64&nbsp;和弦合成音&nbsp;MIDI&nbsp;铃声、原音铃声和&nbsp;MP3/AAC&nbsp;铃声&nbsp;<br />*&nbsp;视频铃声&nbsp;<br />&nbsp;&nbsp;存储功能<br />*&nbsp;高达&nbsp;35&nbsp;MB&nbsp;的用户可用存储空间&nbsp;<br />*&nbsp;通过&nbsp;micro&nbsp;SD&nbsp;存储卡插槽可将存储空间扩展至最高&nbsp;2&nbsp;GB&nbsp;<br />&nbsp;&nbsp;信息功能<br />*&nbsp;在蜂窝网络中实现按键通话&nbsp;<br />*&nbsp;诺基亚&nbsp;Xpress&nbsp;声音信息（AMS）–可便捷快速地通过